"use client"

import * as React from "react"
import * as SheetPrimitive from "@radix-ui/react-dialog"
import { cva, type VariantProps } from "class-variance-authority"
import { X } from "lucide-react"

import { cn } from "@/lib/utils"

const Sheet = SheetPrimitive.Root

const SheetTrigger = SheetPrimitive.Trigger

const SheetClose = SheetPrimitive.Close

const SheetPortal = SheetPrimitive.Portal

const SheetOverlay = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
SheetOverlay.displayName = SheetPrimitive.Overlay.displayName

const sheetVariants = cva(
  "fixed z-50 gap-4 bg-background p-6 shadow-lg transition ease-in-out data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:duration-300 data-[state=open]:duration-500",
  {
    variants: {
      side: {
        top: "inset-x-0 top-0 border-b data-[state=closed]:slide-out-to-top data-[state=open]:slide-in-from-top",
        bottom:
          "inset-x-0 bottom-0 border-t data-[state=closed]:slide-out-to-bottom data-[state=open]:slide-in-from-bottom",
        left: "inset-y-0 left-0 h-full w-3/4 border-r data-[state=closed]:slide-out-to-left data-[state=open]:slide-in-from-left sm:max-w-sm",
        right:
          "inset-y-0 right-0 h-full w-3/4  border-l data-[state=closed]:slide-out-to-right data-[state=open]:slide-in-from-right sm:max-w-sm",
      },
    },
    defaultVariants: {
      side: "right",
    },
  }
)

interface SheetContentProps
  extends React.ComponentPropsWithoutRef<typeof SheetPrimitive.Content>,
    VariantProps<typeof sheetVariants> {}

const SheetContent = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Content>,
  SheetContentProps
>(({ side = "right", className, children, ...props }, ref) => (
  <SheetPortal>
    <SheetOverlay />
    <SheetPrimitive.Content
      ref={ref}
      className={cn(sheetVariants({ side }), className)}
      {...props}
    >
      {children}
      <SheetPrimitive.Close className="absolute right-4 top-4 rounded-sm opacity-70 ring-offset-background transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none data-[state=open]:bg-secondary">
        <X className="h-4 w-4" />
        <span className="sr-only">Close</span>
      </SheetPrimitive.Close>
    </SheetPrimitive.Content>
  </SheetPortal>
))
SheetContent.displayName = SheetPrimitive.Content.displayName

const SheetHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-2 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
SheetHeader.displayName = "SheetHeader"

const SheetFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2",
      className
    )}
    {...props}
  />
)
SheetFooter.displayName = "SheetFooter"

const SheetTitle = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Title>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Title
    ref={ref}
    className={cn("text-lg font-semibold text-foreground", className)}
    {...props}
  />
))
SheetTitle.displayName = SheetPrimitive.Title.displayName

const SheetDescription = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Description>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
SheetDescription.displayName = SheetPrimitive.Description.displayName

export {
  Sheet,
  SheetPortal,
  SheetOverlay,
  SheetTrigger,
  SheetClose,
  SheetContent,
  SheetHeader,
  SheetFooter,
  SheetTitle,
  SheetDescription,
}

client/src/components/ProjectPreview.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { FileText, Folder, Eye, AlertCircle, ChevronDown, ChevronRight, HardDrive, Package, FileCheck2 } from "lucide-react";
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface ProjectFile {
  name: string;
  type: 'file' | 'folder';
  content?: string;
  children?: ProjectFile[];
}

interface ProjectPreviewProps {
  projectName: string;
  configuration: Record<string, any>;
  onDownload: () => void;
  isGenerating?: boolean;
  hideDownloadButton?: boolean;
}

export default function ProjectPreview({ 
  projectName, 
  configuration, 
  onDownload,
  isGenerating = false,
  hideDownloadButton = false
}: ProjectPreviewProps) {
  const [selectedFile, setSelectedFile] = useState<string | null>(null);
  const [expandedFolders, setExpandedFolders] = useState<Set<string>>(new Set());

  // Fetch real project preview data based on user configuration
  const { data: previewData, isLoading, error } = useQuery({
    queryKey: ['/api/project-preview', configuration],
    queryFn: async () => {
      const response = await apiRequest('POST', '/api/project-preview', configuration);
      return await response.json();
    },
    enabled: !!configuration && Object.keys(configuration).length > 0,
    staleTime: 5 * 60 * 1000, // 5 minutes
    retry: 1
  });

  const projectStructure = previewData?.data?.projectStructure || [];
  const sampleFiles = previewData?.data?.sampleFiles || [];
  const totalFiles = previewData?.data?.totalFiles || 0;
  const estimatedSize = previewData?.data?.estimatedSize || 0;
  const keyFiles = previewData?.data?.keyFiles || [];
  const dependencyCount = previewData?.data?.dependencyCount || 0;

  // Handle folder expand/collapse
  const toggleFolder = (folderPath: string) => {
    const newExpanded = new Set(expandedFolders);
    if (newExpanded.has(folderPath)) {
      newExpanded.delete(folderPath);
    } else {
      newExpanded.add(folderPath);
    }
    setExpandedFolders(newExpanded);
  };

  // Handle file selection - try to find content from sample files
  const handleFileClick = (file: ProjectFile, fullPath: string = '') => {
    if (file.type === 'file') {
      // First try to get content from the file itself
      if (file.content) {
        setSelectedFile(file.content);
        return;
      }

      // If no content, try to find it in sample files
      const sampleFile = sampleFiles.find((sf: any) => 
        sf.path.endsWith(file.name) || sf.path.includes(fullPath)
      );

      if (sampleFile) {
        setSelectedFile(sampleFile.content);
      } else {
        setSelectedFile(`// File: ${file.name}\n// Click "Generate and Download Project" to see the full content`);
      }
    } else {
      // If it's a folder, toggle its expanded state
      toggleFolder(fullPath);
    }
  };

  const renderFileTree = (files: ProjectFile[], depth = 0, parentPath = '') => {
    return files.map((file, index) => {
      const currentPath = parentPath ? `${parentPath}/${file.name}` : file.name;
      const isExpanded = expandedFolders.has(currentPath);
      const hasChildren = file.type === 'folder' && file.children && file.children.length > 0;

      return (
        <div key={index} style={{ marginLeft: `${depth * 16}px` }}>
          <div 
            className="flex items-center space-x-1 py-1 px-2 hover:bg-muted/50 rounded cursor-pointer"
            onClick={() => handleFileClick(file, currentPath)}
            data-testid={`${file.type}-${file.name}`}
          >
            {/* Expand/collapse chevron for folders with children */}
            {file.type === 'folder' && hasChildren ? (
              isExpanded ? (
                <ChevronDown className="w-3 h-3 text-muted-foreground flex-shrink-0" />
              ) : (
                <ChevronRight className="w-3 h-3 text-muted-foreground flex-shrink-0" />
              )
            ) : (
              <div className="w-3 h-3 flex-shrink-0" /> /* Spacer for alignment */
            )}

            {/* File/folder icon */}
            {file.type === 'folder' ? (
              <Folder className="w-4 h-4 text-primary flex-shrink-0" />
            ) : (
              <FileText className="w-4 h-4 text-muted-foreground flex-shrink-0" />
            )}

            <span className="text-sm truncate">{file.name}</span>
          </div>

          {/* Render children only if folder is expanded */}
          {file.type === 'folder' && file.children && isExpanded && (
            renderFileTree(file.children, depth + 1, currentPath)
          )}
        </div>
      );
    });
  };

  // Show loading state
  if (isLoading) {
    return (
      <div className="space-y-4">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Eye className="w-5 h-5" />
              <span>Project Preview</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid md:grid-cols-2 gap-4">
              <div>
                <h3 className="text-sm font-medium mb-2">Project Structure</h3>
                <div className="space-y-2">
                  <Skeleton className="h-4 w-full" />
                  <Skeleton className="h-4 w-3/4 ml-4" />
                  <Skeleton className="h-4 w-1/2 ml-8" />
                  <Skeleton className="h-4 w-2/3 ml-4" />
                  <Skeleton className="h-4 w-full" />
                </div>
              </div>
              <div>
                <h3 className="text-sm font-medium mb-2">File Preview</h3>
                <Skeleton className="h-64 w-full" />
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <Skeleton className="h-4 w-1/3 mb-4" />
            <div className="space-y-2">
              <Skeleton className="h-4 w-full" />
              <Skeleton className="h-4 w-2/3" />
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Show error state
  if (error) {
    return (
      <div className="space-y-4">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <AlertCircle className="w-5 h-5 text-destructive" />
              <span>Preview Error</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center justify-center h-64 text-muted-foreground">
              <div className="text-center">
                <AlertCircle className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
                <p className="text-sm">Unable to load project preview.</p>
                <p className="text-xs mt-2">The project will still generate correctly when you download it.</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Format size in KB or MB
  const formatSize = (sizeKB: number) => {
    if (sizeKB > 1024) {
      return `${(sizeKB / 1024).toFixed(2)} MB`;
    }
    return `${sizeKB} KB`;
  };

  return (
    <div className="space-y-4">
      {/* Project Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="p-2 rounded-lg bg-primary/10">
                  <HardDrive className="w-5 h-5 text-primary" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground">Estimated Size</p>
                  <p className="text-lg font-semibold" data-testid="text-estimated-size">
                    {estimatedSize > 0 ? formatSize(estimatedSize) : '~'}
                  </p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="p-2 rounded-lg bg-primary/10">
                  <FileCheck2 className="w-5 h-5 text-primary" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground">Key Files</p>
                  <p className="text-lg font-semibold" data-testid="text-key-files">
                    {keyFiles.length}
                  </p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="p-2 rounded-lg bg-primary/10">
                  <Package className="w-5 h-5 text-primary" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground">Total Files</p>
                  <p className="text-lg font-semibold" data-testid="text-total-files">
                    {totalFiles}
                  </p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="p-2 rounded-lg bg-primary/10">
                  <Package className="w-5 h-5 text-primary" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground">Dependencies</p>
                  <p className="text-lg font-semibold" data-testid="text-dependencies">
                    {dependencyCount}
                  </p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Eye className="w-5 h-5" />
            <span>Project Preview</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid md:grid-cols-2 gap-4">
            {/* File Tree */}
            <div>
              <h3 className="text-sm font-medium mb-2">Project Structure</h3>
              <ScrollArea className="h-64 border rounded-md p-2">
                {projectStructure.length > 0 ? (
                  renderFileTree(projectStructure)
                ) : (
                  <div className="flex items-center justify-center h-full text-muted-foreground text-sm">
                    Project structure will appear here
                  </div>
                )}
              </ScrollArea>
            </div>

            {/* File Content */}
            <div>
              <h3 className="text-sm font-medium mb-2">File Preview</h3>
              <ScrollArea className="h-64 border rounded-md p-2">
                {selectedFile ? (
                  <pre className="text-xs font-mono whitespace-pre-wrap">
                    {selectedFile}
                  </pre>
                ) : (
                  <div className="flex items-center justify-center h-full text-muted-foreground text-sm">
                    Click on a file to preview its content
                  </div>
                )}
              </ScrollArea>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Configuration Summary */}
      <Card>
        <CardHeader>
          <CardTitle>Configuration Summary</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {Object.entries(configuration).map(([key, value]) => {
              // Skip empty or undefined values
              if (value === "" || value === null || value === undefined) {
                return null;
              }

              // Handle utilities object specially
              if (key === 'utilities' && typeof value === 'object' && !Array.isArray(value)) {
                const selectedUtilities = Object.entries(value)
                  .filter(([_, enabled]) => enabled === true)
                  .map(([utilityKey, _]) => utilityKey);

                // Don't show if no utilities selected
                if (selectedUtilities.length === 0) {
                  return null;
                }

                return (
                  <div key={key} className="flex items-center justify-between">
                    <span className="text-sm font-medium capitalize">
                      {key.replace(/([A-Z])/g, ' $1').toLowerCase()}
                    </span>
                    <div className="flex flex-wrap gap-1 justify-end">
                      {selectedUtilities.map((utility, index) => (
                        <Badge key={index} variant="secondary" className="text-xs">
                          {utility.replace(/([A-Z])/g, ' $1').toLowerCase()}
                        </Badge>
                      ))}
                    </div>
                  </div>
                );
              }

              return (
                <div key={key} className="flex items-center justify-between">
                  <span className="text-sm font-medium capitalize">
                    {key.replace(/([A-Z])/g, ' $1').toLowerCase()}
                  </span>
                  <div className="flex flex-wrap gap-1">
                    {Array.isArray(value) ? (
                      value.map((item: string, index: number) => (
                        <Badge key={index} variant="secondary" className="text-xs">
                          {item}
                        </Badge>
                      ))
                    ) : typeof value === 'object' ? (
                      // For other objects, convert to JSON or show object structure
                      <Badge variant="secondary" className="text-xs">
                        {JSON.stringify(value)}
                      </Badge>
                    ) : (
                      <Badge variant="secondary" className="text-xs">
                        {String(value)}
                      </Badge>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

