import { useState, useEffect, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { AlertCircle, CheckCircle2, Package, Keyboard, RefreshCw, Download, Github, Plus, Lock, Globe, ExternalLink, Loader2 } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import ProgressBar from "./ProgressBar";
import WizardStep from "./WizardStep";
import ProjectPreview from "./ProjectPreview";
import HelpTooltip from "./HelpTooltip";
import { ErrorAlert } from "./ErrorAlert";
import { getHelpContent } from "@/lib/helpContent";
import { WizardValidator, validationLabels } from "../../../shared/validationMatrix";
import { useConfigPersistence } from "@/hooks/useConfigPersistence";
import { ResumeDialog } from "./ResumeDialog";
import { ErrorMessages, ErrorTitles, getErrorMessage, getErrorTitle } from "@/lib/errorMessages";

interface GitHubOptions {
  repoName: string;
  isNewRepo: boolean;
  isPrivate: boolean;
}

interface WizardConfig {
  testingType: string;
  framework: string;
  language: string;
  testingPattern: string;
  testRunner: string;
  buildTool: string;
  projectName: string;
  groupId?: string;
  artifactId?: string;
  cicdTool: string;
  reportingTool: string;
  includeSampleTests: boolean;
  utilities: {
    configReader: boolean;
    jsonReader: boolean;
    screenshotUtility: boolean;
    logger: boolean;
    dataProvider: boolean;
  };
  dependencies: string[];
  deliveryMethod?: 'download' | 'github';
  githubOptions?: GitHubOptions;
}

interface WizardProps {
  onComplete: (config: WizardConfig) => void;
  onBack: () => void;
}

interface DependenciesStepProps {
  config: WizardConfig;
  onNext: () => void;
  onPrevious: () => void;
  stepNumber: number;
  totalSteps: number;
}

function DependenciesStep({ config, onNext, onPrevious, stepNumber, totalSteps }: DependenciesStepProps) {
  const { data: dependenciesData, isLoading, error, refetch } = useQuery({
    queryKey: ['/api/project-dependencies', config],
    queryFn: async () => {
      const response = await apiRequest('POST', '/api/project-dependencies', config);
      return await response.json();
    },
    enabled: !!config.testingType && !!config.framework && !!config.language && !!config.testRunner && !!config.buildTool,
    staleTime: 5 * 60 * 1000,
    retry: 1
  });

  const dependencies = dependenciesData?.data?.dependencies || {};
  const buildTool = dependenciesData?.data?.buildTool || config.buildTool;
  const language = dependenciesData?.data?.language || config.language;

  const getBuildToolLabel = (tool: string): string => {
    const labels: Record<string, string> = {
      'maven': 'Maven',
      'gradle': 'Gradle',
      'npm': 'npm',
      'pip': 'pip',
      'nuget': 'NuGet',
      'dotnet-cli': '.NET CLI',
      'spm': 'Swift Package Manager'
    };
    return labels[tool] || tool;
  };

  return (
    <WizardStep
      stepNumber={stepNumber}
      totalSteps={totalSteps}
      title="Project Dependencies"
      description="Review the dependencies that will be included in your project"
      onNext={onNext}
      onPrevious={onPrevious}
      showSkip={false}
    >
      <div className="space-y-6">
        {isLoading ? (
          <div className="space-y-3">
            <Skeleton className="h-12 w-full rounded-lg" />
            <Skeleton className="h-12 w-full rounded-lg" />
            <Skeleton className="h-12 w-full rounded-lg" />
            <Skeleton className="h-12 w-full rounded-lg" />
          </div>
        ) : error ? (
          <ErrorAlert
            title={ErrorTitles.DEPENDENCIES}
            message={ErrorMessages.DEPENDENCIES_LOAD_FAILED}
            onRetry={() => refetch()}
            showRetry={true}
          />
        ) : Object.keys(dependencies).length === 0 ? (
          <div className="text-center py-12 px-4">
            <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-muted/30 mb-4">
              <Package className="w-8 h-8 text-muted-foreground" />
            </div>
            <p className="text-sm font-medium text-muted-foreground">Dependencies auto-configured</p>
            <p className="text-xs text-muted-foreground mt-1">Dependencies will be automatically set up during project generation</p>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="flex items-center gap-2 p-3 rounded-lg bg-primary/5 border border-primary/20">
              <Package className="w-4 h-4 text-primary" />
              <span className="text-sm">
                Build Tool: <span className="font-semibold text-primary">{getBuildToolLabel(buildTool)}</span>
              </span>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {Object.entries(dependencies).map(([name, version]) => (
                <div 
                  key={name} 
                  className="flex items-center justify-between p-4 rounded-lg border bg-card hover-elevate"
                  data-testid={`dependency-${name}`}
                >
                  <div className="flex items-center gap-2">
                    <div className="w-2 h-2 rounded-full bg-primary"></div>
                    <span className="text-sm font-medium">{name}</span>
                  </div>
                  <Badge variant="secondary" className="text-xs font-mono">
                    v{version as string}
                  </Badge>
                </div>
              ))}
            </div>

            <div className="flex items-start gap-3 p-4 bg-muted/30 rounded-lg">
              <CheckCircle2 className="w-5 h-5 text-primary flex-shrink-0 mt-0.5" />
              <div>
                <p className="text-sm font-medium">Auto-configured dependencies</p>
                <p className="text-xs text-muted-foreground mt-1">
                  These {Object.keys(dependencies).length} {Object.keys(dependencies).length === 1 ? 'dependency' : 'dependencies'} will be automatically included in your {getBuildToolLabel(buildTool)} configuration file
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </WizardStep>
  );
}

export default function Wizard({ onComplete, onBack }: WizardProps) {
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState(0);
  const [completedSteps, setCompletedSteps] = useState<number[]>([]);
  const [isGenerating, setIsGenerating] = useState(false);
  const [deliveryMethod, setDeliveryMethod] = useState<'download' | 'github'>('download');
  const [githubRepoName, setGithubRepoName] = useState('');
  const [githubIsNewRepo, setGithubIsNewRepo] = useState(true);
  const [githubIsPrivate, setGithubIsPrivate] = useState(false);
  const [selectedExistingRepo, setSelectedExistingRepo] = useState('');
  const [config, setConfig] = useState<WizardConfig>({
    testingType: "",
    framework: "",
    language: "",
    testingPattern: "",
    testRunner: "",
    buildTool: "",
    projectName: "",
    groupId: "",
    artifactId: "",
    cicdTool: "",
    reportingTool: "",
    includeSampleTests: true,
    utilities: {
      configReader: true,
      jsonReader: false,
      screenshotUtility: true,
      logger: true,
      dataProvider: false,
    },
    dependencies: [],
    deliveryMethod: 'download'
  });

  // GitHub status query
  const { data: githubStatus, isLoading: githubStatusLoading } = useQuery({
    queryKey: ['/api/github/status'],
    staleTime: 30 * 1000,
  });

  // GitHub repos query (only when connected and selecting existing)
  const { data: githubReposData, isLoading: githubReposLoading } = useQuery({
    queryKey: ['/api/github/repos'],
    enabled: githubStatus?.connected && !githubIsNewRepo,
    staleTime: 60 * 1000,
  });

  // Sync repo name with project name when creating new repo
  useEffect(() => {
    if (githubIsNewRepo && config.projectName) {
      setGithubRepoName(config.projectName);
    }
  }, [config.projectName, githubIsNewRepo]);

  // Configuration persistence
  const persistence = useConfigPersistence();
  const [showResumeDialog, setShowResumeDialog] = useState(false);
  const [savedTimestamp, setSavedTimestamp] = useState(0);
  const isInitialMount = useRef(true);
  const hasResumedOrStartedFresh = useRef(false);

  // Check for saved configuration on mount
  useEffect(() => {
    if (isInitialMount.current) {
      const saved = persistence.loadConfig();
      if (saved) {
        setSavedTimestamp(saved.timestamp);
        setShowResumeDialog(true);
      } else {
        // No saved config, allow auto-save immediately
        hasResumedOrStartedFresh.current = true;
      }
      isInitialMount.current = false;
    }
  }, []);

  // Auto-save configuration on changes (debounced)
  // Only save after user has explicitly resumed or started fresh
  useEffect(() => {
    if (hasResumedOrStartedFresh.current && !showResumeDialog) {
      const timer = setTimeout(() => {
        persistence.saveConfig(config, currentStep);
      }, 500);
      return () => clearTimeout(timer);
    }
  }, [config, currentStep, showResumeDialog]);

  // Handle resume configuration
  const handleResume = () => {
    const saved = persistence.loadConfig();
    if (saved) {
      setConfig(saved.config as WizardConfig);
      setCurrentStep(saved.currentStep);
      toast({
        title: "Configuration Restored",
        description: `Your previous configuration has been loaded. Resuming at step ${saved.currentStep + 1}.`,
      });
    }
    hasResumedOrStartedFresh.current = true;
    setShowResumeDialog(false);
  };

  // Handle start fresh
  const handleStartFresh = () => {
    persistence.clearConfig();
    hasResumedOrStartedFresh.current = true;
    setShowResumeDialog(false);
    toast({
      title: "Starting Fresh",
      description: "Previous configuration cleared.",
    });
  };

  const steps = [
    "Testing Type",
    "Framework",
    "Language", 
    "Testing Pattern",
    "Test Runner",
    "Build Tool",
    "Project Metadata",
    "CI/CD",
    "Reporting",
    "Utilities",
    "Dependencies",
    "Summary"
  ];

  const validateStep = (step: number): boolean => {
    switch (step) {
      case 0: return config.testingType !== "";
      case 1: return config.framework !== "";
      case 2: return config.language !== "";
      case 3: return config.testingPattern !== "";
      case 4: return config.testRunner !== "";
      case 5: return config.buildTool !== "";
      case 6: {
        // Project metadata validation with regex enforcement
        const projectName = config.projectName.trim();

        // Validate project name
        if (!projectName) return false;
        if (!/^[a-zA-Z0-9_-]+$/.test(projectName)) return false;
        if (projectName.length > 100) return false;

        // For Maven projects, also validate groupId and artifactId
        if (config.buildTool === "maven") {
          const groupId = config.groupId?.trim() || "";
          const artifactId = config.artifactId?.trim() || "";

          if (!groupId || !/^[a-zA-Z0-9._-]+$/.test(groupId)) return false;
          if (!artifactId || !/^[a-zA-Z0-9_-]+$/.test(artifactId)) return false;
        }

        return true;
      }
      case 7: return true; // CI/CD is optional, so always valid
      case 8: return true; // Reporting is optional, so always valid
      default: return true;
    }
  };

  // Update config and reset invalid downstream selections
  const updateConfig = (key: string, value: any) => {
    const newConfig = { ...config, [key]: value };
    const validatedConfig = WizardValidator.resetInvalidSelections(newConfig);
    setConfig(validatedConfig);

    // Show toast if selections were reset
    if (JSON.stringify(newConfig) !== JSON.stringify(validatedConfig)) {
      toast({
        title: "Selections Updated",
        description: "Some selections were automatically updated due to compatibility requirements.",
        variant: "default",
      });
    }
  };

  // Get filtered options for current step
  const getFilteredOptions = (step: string): string[] => {
    return WizardValidator.getFilteredOptions(step, config);
  };

  const handleNext = () => {
    if (!validateStep(currentStep)) {
      toast({
        title: "Validation Error",
        description: "Please complete all required fields before proceeding.",
        variant: "destructive",
      });
      return;
    }

    // Mark current step as completed
    if (!completedSteps.includes(currentStep)) {
      setCompletedSteps(prev => [...prev, currentStep]);
    }

    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      handleProjectGeneration();
    }
  };

  const handleProjectGeneration = async () => {
    setIsGenerating(true);
    try {
      const finalConfig: WizardConfig = {
        ...config,
        deliveryMethod,
        githubOptions: deliveryMethod === 'github' ? {
          repoName: githubIsNewRepo ? githubRepoName : selectedExistingRepo,
          isNewRepo: githubIsNewRepo,
          isPrivate: githubIsPrivate,
        } : undefined,
      };
      await onComplete(finalConfig);
    } catch (error) {
      const errorTitle = getErrorTitle(error);
      const errorMessage = getErrorMessage(error);

      toast({
        title: errorTitle,
        description: errorMessage,
        variant: "destructive",
        action: (
          <Button
            variant="outline"
            size="sm"
            onClick={handleProjectGeneration}
            className="gap-2"
          >
            <RefreshCw className="w-3 h-3" />
            Retry
          </Button>
        ),
      });
    } finally {
      setIsGenerating(false);
    }
  };

  const handleStepNavigation = (stepIndex: number) => {
    // Only allow navigation to completed steps or the next immediate step
    if (stepIndex <= currentStep || completedSteps.includes(stepIndex)) {
      setCurrentStep(stepIndex);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };


  const toggleArrayValue = (array: string[], value: string): string[] => {
    return array.includes(value)
      ? array.filter(item => item !== value)
      : [...array, value];
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 0: // Testing Type
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Testing Type"
            description="Select the primary type of testing for your project"
            onNext={handleNext}
            onPrevious={currentStep > 0 ? handlePrevious : onBack}
            canGoPrevious={true}
          >
            <div className="space-y-4">
              <RadioGroup 
                value={config.testingType} 
                onValueChange={(value) => updateConfig("testingType", value)}
              >
                {Object.entries(validationLabels.testingTypes).map(([key, label]) => (
                  <div key={key} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={key} 
                      id={key}
                      data-testid={`radio-${key}`}
                    />
                    <Label htmlFor={key}>{label}</Label>
                  </div>
                ))}
              </RadioGroup>

              {config.testingType && (
                <div className="mt-4 p-3 bg-muted/50 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <CheckCircle2 className="h-4 w-4 text-green-600" />
                    <span className="text-sm font-medium">Available Frameworks:</span>
                  </div>
                  <div className="flex flex-wrap gap-1">
                    {getFilteredOptions('framework').map(framework => (
                      <Badge key={framework} variant="secondary" className="text-xs">
                        {validationLabels.frameworks[framework as keyof typeof validationLabels.frameworks]}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </WizardStep>
        );

      case 1: { // Framework
        const availableFrameworks = getFilteredOptions('framework');
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Testing Framework"
            description="Choose the testing framework for your project"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              {!config.testingType && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                  <AlertCircle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    Please select a testing type first to see available frameworks.
                  </span>
                </div>
              )}

              <RadioGroup 
                value={config.framework} 
                onValueChange={(value) => updateConfig("framework", value)}
                disabled={!config.testingType}
              >
                {availableFrameworks.map((framework) => (
                  <div key={framework} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={framework} 
                      id={framework}
                      data-testid={`radio-${framework}`}
                    />
                    <Label htmlFor={framework}>{validationLabels.frameworks[framework as keyof typeof validationLabels.frameworks]}</Label>
                  </div>
                ))}
              </RadioGroup>

              {config.framework && (
                <div className="mt-4 p-3 bg-muted/50 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <CheckCircle2 className="h-4 w-4 text-green-600" />
                    <span className="text-sm font-medium">Available Languages:</span>
                  </div>
                  <div className="flex flex-wrap gap-1">
                    {getFilteredOptions('language').map(language => (
                      <Badge key={language} variant="secondary" className="text-xs">
                        {validationLabels.languages[language as keyof typeof validationLabels.languages]}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </WizardStep>
        );
      }

      case 2: { // Language
        const availableLanguages = getFilteredOptions('language');
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Programming Language"
            description="Select the programming language for your project"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              {!config.framework && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                  <AlertCircle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    Please select a testing framework first to see compatible languages.
                  </span>
                </div>
              )}

              <RadioGroup
                value={config.language}
                onValueChange={(value) => updateConfig("language", value)}
                disabled={!config.framework}
              >
                {availableLanguages.map((language) => (
                  <div key={language} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={language} 
                      id={language}
                      data-testid={`radio-${language}`}
                    />
                    <Label htmlFor={language}>{validationLabels.languages[language as keyof typeof validationLabels.languages]}</Label>
                  </div>
                ))}
              </RadioGroup>

              {config.language && (
                <div className="mt-4 p-3 bg-muted/50 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <CheckCircle2 className="h-4 w-4 text-green-600" />
                    <span className="text-sm font-medium">Available Test Runners:</span>
                  </div>
                  <div className="flex flex-wrap gap-1">
                    {getFilteredOptions('testRunner').map(runner => (
                      <Badge key={runner} variant="secondary" className="text-xs">
                        {validationLabels.testRunners[runner as keyof typeof validationLabels.testRunners]}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </WizardStep>
        );
      }

      case 3: { // Testing Pattern
        const availableTestingPatterns = getFilteredOptions('testingPattern');
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Testing Pattern"
            description="Choose your testing architecture pattern"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              {!config.framework && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                  <AlertCircle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    Please select a testing framework first to see compatible patterns.
                  </span>
                </div>
              )}

              <RadioGroup
                value={config.testingPattern}
                onValueChange={(value) => updateConfig("testingPattern", value)}
                disabled={!config.framework}
              >
                {availableTestingPatterns.map((pattern) => (
                  <div key={pattern} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={pattern} 
                      id={pattern}
                      data-testid={`radio-${pattern}`}
                    />
                    <Label htmlFor={pattern} className="capitalize flex items-center">
                      {pattern.replace(/-/g, ' ')}
                      <HelpTooltip content={getHelpContent(pattern)} />
                    </Label>
                  </div>
                ))}
              </RadioGroup>
            </div>
          </WizardStep>
        );
      }

      case 4: { // Test Runner
        const availableTestRunners = getFilteredOptions('testRunner');
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Test Runner"
            description="Choose your test runner"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              {!config.language && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                  <AlertCircle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    Please select a programming language first to see compatible test runners.
                  </span>
                </div>
              )}

              <RadioGroup
                value={config.testRunner}
                onValueChange={(value) => updateConfig("testRunner", value)}
                disabled={!config.language}
              >
                {availableTestRunners.map((runner) => (
                  <div key={runner} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={runner} 
                      id={runner}
                      data-testid={`radio-${runner}`}
                    />
                    <Label htmlFor={runner} className="flex items-center">
                      {validationLabels.testRunners[runner as keyof typeof validationLabels.testRunners]}
                      <HelpTooltip content={getHelpContent(runner)} />
                    </Label>
                  </div>
                ))}
              </RadioGroup>

              {config.testRunner && (
                <div className="mt-4 p-3 bg-muted/50 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <CheckCircle2 className="h-4 w-4 text-green-600" />
                    <span className="text-sm font-medium">Available Build Tools:</span>
                  </div>
                  <div className="flex flex-wrap gap-1">
                    {getFilteredOptions('buildTool').map(tool => (
                      <Badge key={tool} variant="secondary" className="text-xs">
                        {validationLabels.buildTools[tool as keyof typeof validationLabels.buildTools]}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </WizardStep>
        );
      }

      case 5: { // Build Tool
        const availableBuildTools = getFilteredOptions('buildTool');
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Build Tool"
            description="Choose your build tool"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              {!config.language && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                  <AlertCircle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    Please select a programming language first to see compatible build tools.
                  </span>
                </div>
              )}

              <RadioGroup
                value={config.buildTool}
                onValueChange={(value) => updateConfig("buildTool", value)}
                disabled={!config.language}
              >
                {availableBuildTools.map((tool) => (
                  <div key={tool} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={tool} 
                      id={tool}
                      data-testid={`radio-${tool}`}
                    />
                    <Label htmlFor={tool} className="flex items-center">
                      {validationLabels.buildTools[tool as keyof typeof validationLabels.buildTools]}
                      <HelpTooltip content={getHelpContent(tool)} />
                    </Label>
                  </div>
                ))}
              </RadioGroup>
            </div>
          </WizardStep>
        );
      }

      case 6: { // Project Metadata
        const projectNameError = (() => {
          const name = config.projectName.trim();
          if (!name) return "Project name is required";
          if (!/^[a-zA-Z0-9_-]+$/.test(name)) {
            return "Project name can only contain letters, numbers, hyphens, and underscores";
          }
          if (name.length > 100) return "Project name must be less than 100 characters";
          return "";
        })();

        const groupIdError = config.buildTool === "maven" && (() => {
          const groupId = config.groupId?.trim() || "";
          if (!groupId) return "Group ID is required for Maven projects";
          if (!/^[a-zA-Z0-9._-]+$/.test(groupId)) {
            return "Group ID must follow reverse domain notation (e.g., com.example.app)";
          }
          return "";
        })();

        const artifactIdError = config.buildTool === "maven" && (() => {
          const artifactId = config.artifactId?.trim() || "";
          if (!artifactId) return "Artifact ID is required for Maven projects";
          if (!/^[a-zA-Z0-9_-]+$/.test(artifactId)) {
            return "Artifact ID can only contain letters, numbers, hyphens, and underscores";
          }
          return "";
        })();

        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Project Metadata"
            description="Configure your project details"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              <div>
                <Label htmlFor="projectName">Project Name *</Label>
                <Input
                  id="projectName"
                  value={config.projectName}
                  onChange={(e) => updateConfig("projectName", e.target.value)}
                  placeholder="QA Automation Project"
                  data-testid="input-project-name"
                  className={projectNameError ? "border-destructive" : ""}
                />
                {projectNameError && (
                  <p className="text-sm text-destructive mt-1 flex items-center gap-1" data-testid="error-project-name">
                    <AlertCircle className="h-3 w-3" />
                    {projectNameError}
                  </p>
                )}
              </div>
              {config.buildTool === "maven" && (
                <>
                  <div>
                    <Label htmlFor="groupId">Group ID *</Label>
                    <Input
                      id="groupId"
                      value={config.groupId}
                      onChange={(e) => updateConfig("groupId", e.target.value)}
                      placeholder="com.qastarter"
                      data-testid="input-group-id"
                      className={groupIdError ? "border-destructive" : ""}
                    />
                    {groupIdError && (
                      <p className="text-sm text-destructive mt-1 flex items-center gap-1" data-testid="error-group-id">
                        <AlertCircle className="h-3 w-3" />
                        {groupIdError}
                      </p>
                    )}
                  </div>
                  <div>
                    <Label htmlFor="artifactId">Artifact ID *</Label>
                    <Input
                      id="artifactId"
                      value={config.artifactId}
                      onChange={(e) => updateConfig("artifactId", e.target.value)}
                      placeholder="qa-automation"
                      data-testid="input-artifact-id"
                      className={artifactIdError ? "border-destructive" : ""}
                    />
                    {artifactIdError && (
                      <p className="text-sm text-destructive mt-1 flex items-center gap-1" data-testid="error-artifact-id">
                        <AlertCircle className="h-3 w-3" />
                        {artifactIdError}
                      </p>
                    )}
                  </div>
                </>
              )}
            </div>
          </WizardStep>
        );
      }

      case 7: { // CI/CD
        const availableCicdTools = getFilteredOptions('cicdTool');
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="CI/CD Integration"
            description="Select a CI/CD tool for your project"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              {!config.framework && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                  <AlertCircle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    Please select a testing framework first to see compatible CI/CD tools.
                  </span>
                </div>
              )}

              <RadioGroup
                value={config.cicdTool}
                onValueChange={(value) => updateConfig("cicdTool", value)}
                disabled={!config.framework}
              >
                {availableCicdTools.map((tool) => (
                  <div key={tool} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={tool} 
                      id={tool}
                      data-testid={`radio-${tool}`}
                    />
                    <Label htmlFor={tool} className="capitalize flex items-center">
                      {tool.replace(/-/g, ' ')}
                      <HelpTooltip content={getHelpContent(tool)} />
                    </Label>
                  </div>
                ))}
              </RadioGroup>
            </div>
          </WizardStep>
        );
      }

      case 8: { // Reporting
        const availableReportingTools = getFilteredOptions('reportingTool');
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Reporting Tools"
            description="Select a reporting tool for your project"
            onNext={handleNext}
            onPrevious={handlePrevious}
          >
            <div className="space-y-4">
              {!config.framework && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                  <AlertCircle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    Please select a testing framework first to see compatible reporting tools.
                  </span>
                </div>
              )}

              <RadioGroup
                value={config.reportingTool}
                onValueChange={(value) => updateConfig("reportingTool", value)}
                disabled={!config.framework}
              >
                {availableReportingTools.map((tool) => (
                  <div key={tool} className="flex items-center space-x-2">
                    <RadioGroupItem 
                      value={tool} 
                      id={tool}
                      data-testid={`radio-${tool}`}
                    />
                    <Label htmlFor={tool} className="capitalize flex items-center">
                      {tool.replace(/-/g, ' ')}
                      <HelpTooltip content={getHelpContent(tool)} />
                    </Label>
                  </div>
                ))}
              </RadioGroup>
            </div>
          </WizardStep>
        );
      }

      case 9: // Utilities
        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Project Options & Utilities"
            description="Configure sample tests and select helpful utility components"
            onNext={handleNext}
            onPrevious={handlePrevious}
            showSkip={false}
          >
            <div className="space-y-4">
              {/* Sample Tests Toggle */}
              <div className="flex items-center justify-between gap-3 p-3 rounded-lg border bg-card/50">
                <div className="flex-1">
                  <Label htmlFor="includeSampleTests" className="text-sm font-medium cursor-pointer flex items-center gap-1">
                    Include Sample Tests
                    <HelpTooltip content={getHelpContent('sample-tests')} />
                  </Label>
                  <p className="text-xs text-muted-foreground mt-0.5">
                    Include example test files demonstrating best practices
                  </p>
                </div>
                <Switch
                  id="includeSampleTests"
                  checked={config.includeSampleTests}
                  onCheckedChange={(checked) => updateConfig("includeSampleTests", checked)}
                  data-testid="switch-include-sample-tests"
                />
              </div>

              {/* Utilities Section */}
              <div className="space-y-1.5">
                <h4 className="text-xs font-medium text-muted-foreground uppercase tracking-wide">Utility Components</h4>
                <div className="space-y-2">
                  {Object.entries({
                    configReader: { 
                      name: "Configuration Reader", 
                      description: "Manage test environment configurations" 
                    },
                    jsonReader: { 
                      name: "JSON Reader", 
                      description: "Parse and read JSON data files" 
                    },
                    screenshotUtility: { 
                      name: "Screenshot Utility", 
                      description: "Capture screenshots during test execution" 
                    },
                    logger: { 
                      name: "Logger", 
                      description: "Enhanced logging for debugging" 
                    },
                    dataProvider: { 
                      name: "Data Provider", 
                      description: "Manage test data providers" 
                    }
                  }).map(([key, { name, description }]) => (
                    <div key={key} className="flex items-center justify-between gap-3 p-2.5 rounded-md border bg-card">
                      <div className="flex-1 min-w-0">
                        <Label htmlFor={key} className="text-sm font-medium cursor-pointer">{name}</Label>
                        <p className="text-xs text-muted-foreground">{description}</p>
                      </div>
                      <Switch
                        id={key}
                        checked={config.utilities[key as keyof typeof config.utilities]}
                        onCheckedChange={(checked) =>
                          updateConfig("utilities", { ...config.utilities, [key]: checked })
                        }
                        data-testid={`switch-utility-${key}`}
                      />
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </WizardStep>
        );

      case 10: // Dependencies
        return <DependenciesStep config={config} onNext={handleNext} onPrevious={handlePrevious} stepNumber={currentStep} totalSteps={steps.length} />;

      case 11: // Summary
        const isGithubReady = deliveryMethod === 'github' && (
          (githubIsNewRepo && githubRepoName.trim()) ||
          (!githubIsNewRepo && selectedExistingRepo)
        );
        const canGenerate = !isGenerating && (deliveryMethod === 'download' || (githubStatus?.connected && isGithubReady));

        return (
          <WizardStep
            stepNumber={currentStep}
            totalSteps={steps.length}
            title="Summary"
            description="Review your configuration and choose how to get your project"
            onNext={handleProjectGeneration}
            onPrevious={handlePrevious}
            isLastStep={true}
            canGoNext={canGenerate}
          >
            <div className="space-y-6">
              <ProjectPreview
                projectName={config.projectName}
                configuration={config}
                onDownload={handleProjectGeneration}
                isGenerating={isGenerating}
                hideDownloadButton={true}
              />

              <div className="border-t pt-6">
                <h3 className="text-lg font-semibold mb-4">Delivery Method</h3>
                <Tabs value={deliveryMethod} onValueChange={(v) => setDeliveryMethod(v as 'download' | 'github')}>
                  <TabsList className="grid w-full grid-cols-2">
                    <TabsTrigger value="download" className="gap-2" data-testid="tab-download">
                      <Download className="w-4 h-4" />
                      Download ZIP
                    </TabsTrigger>
                    <TabsTrigger value="github" className="gap-2" data-testid="tab-github">
                      <Github className="w-4 h-4" />
                      Push to GitHub
                    </TabsTrigger>
                  </TabsList>

                  <TabsContent value="download" className="mt-4">
                    <div className="flex items-start gap-3 p-4 bg-muted/30 rounded-lg">
                      <Download className="w-5 h-5 text-primary flex-shrink-0 mt-0.5" />
                      <div>
                        <p className="text-sm font-medium">Download as ZIP file</p>
                        <p className="text-xs text-muted-foreground mt-1">
                          Your project will be downloaded as a ZIP archive that you can extract and use locally.
                        </p>
                      </div>
                    </div>
                  </TabsContent>

                  <TabsContent value="github" className="mt-4 space-y-4">
                    {githubStatusLoading ? (
                      <div className="flex items-center justify-center p-8">
                        <Loader2 className="w-6 h-6 animate-spin text-muted-foreground" />
                      </div>
                    ) : !githubStatus?.connected ? (
                      <div className="border rounded-lg p-6 text-center space-y-4">
                        <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-muted/50 mx-auto">
                          <Github className="w-8 h-8 text-muted-foreground" />
                        </div>
                        <div className="space-y-2">
                          <h4 className="text-lg font-semibold">Connect Your GitHub Account</h4>
                          <p className="text-sm text-muted-foreground max-w-md mx-auto">
                            Push your generated project directly to a GitHub repository. Create new repos or update existing ones with a single click.
                          </p>
                        </div>
                        <div className="space-y-3">
                          <Button
                            variant="default"
                            className="gap-2"
                            onClick={() => {
                              window.open('https://replit.com/~/integrations', '_blank');
                            }}
                            data-testid="button-connect-github"
                          >
                            <Github className="w-4 h-4" />
                            Connect GitHub
                          </Button>
                          <p className="text-xs text-muted-foreground">
                            Opens Replit integrations in a new tab. After connecting, return here and refresh.
                          </p>
                        </div>
                        <div className="pt-2 border-t">
                          <div className="flex items-center justify-center gap-4 text-xs text-muted-foreground">
                            <span className="flex items-center gap-1">
                              <CheckCircle2 className="w-3 h-3 text-green-500" />
                              Secure OAuth
                            </span>
                            <span className="flex items-center gap-1">
                              <CheckCircle2 className="w-3 h-3 text-green-500" />
                              No tokens to manage
                            </span>
                            <span className="flex items-center gap-1">
                              <CheckCircle2 className="w-3 h-3 text-green-500" />
                              One-click setup
                            </span>
                          </div>
                        </div>
                      </div>
                    ) : (
                      <>
                        <div className="flex items-center gap-3 p-3 bg-green-50 dark:bg-green-900/20 rounded-lg">
                          <CheckCircle2 className="w-4 h-4 text-green-600" />
                          <span className="text-sm text-green-700 dark:text-green-300">
                            Connected as <strong>{githubStatus.user?.login}</strong>
                          </span>
                        </div>

                        <RadioGroup
                          value={githubIsNewRepo ? 'new' : 'existing'}
                          onValueChange={(v) => {
                            const isNew = v === 'new';
                            setGithubIsNewRepo(isNew);
                            if (isNew) {
                              setSelectedExistingRepo('');
                            }
                          }}
                          className="space-y-3"
                        >
                          <div className="flex items-center space-x-2">
                            <RadioGroupItem value="new" id="repo-new" data-testid="radio-repo-new" />
                            <Label htmlFor="repo-new" className="flex items-center gap-2">
                              <Plus className="w-4 h-4" />
                              Create new repository
                            </Label>
                          </div>
                          <div className="flex items-center space-x-2">
                            <RadioGroupItem value="existing" id="repo-existing" data-testid="radio-repo-existing" />
                            <Label htmlFor="repo-existing">Select existing repository</Label>
                          </div>
                        </RadioGroup>

                        {githubIsNewRepo ? (
                          <div className="space-y-4">
                            <div className="space-y-2">
                              <Label htmlFor="github-repo-name">Repository Name</Label>
                              <Input
                                id="github-repo-name"
                                value={githubRepoName}
                                onChange={(e) => setGithubRepoName(e.target.value)}
                                placeholder="my-qa-project"
                                data-testid="input-github-repo-name"
                              />
                            </div>

                            <div className="flex items-center justify-between p-3 rounded-md border bg-card">
                              <div className="flex items-center gap-2">
                                {githubIsPrivate ? (
                                  <Lock className="w-4 h-4 text-muted-foreground" />
                                ) : (
                                  <Globe className="w-4 h-4 text-muted-foreground" />
                                )}
                                <Label htmlFor="github-private" className="cursor-pointer">
                                  {githubIsPrivate ? 'Private repository' : 'Public repository'}
                                </Label>
                              </div>
                              <Switch
                                id="github-private"
                                checked={githubIsPrivate}
                                onCheckedChange={setGithubIsPrivate}
                                data-testid="switch-github-private"
                              />
                            </div>
                          </div>
                        ) : (
                          <div className="space-y-2">
                            <Label>Select Repository</Label>
                            {githubReposLoading ? (
                              <div className="space-y-2">
                                <Skeleton className="h-10 w-full" />
                              </div>
                            ) : (
                              <Select
                                value={selectedExistingRepo}
                                onValueChange={setSelectedExistingRepo}
                              >
                                <SelectTrigger data-testid="select-github-repo">
                                  <SelectValue placeholder="Choose a repository" />
                                </SelectTrigger>
                                <SelectContent>
                                  {githubReposData?.data?.map((repo: any) => (
                                    <SelectItem key={repo.id} value={repo.name} data-testid={`select-repo-${repo.name}`}>
                                      <div className="flex items-center gap-2">
                                        {repo.private ? (
                                          <Lock className="w-3 h-3 text-muted-foreground" />
                                        ) : (
                                          <Globe className="w-3 h-3 text-muted-foreground" />
                                        )}
                                        <span>{repo.name}</span>
                                      </div>
                                    </SelectItem>
                                  ))}
                                </SelectContent>
                              </Select>
                            )}
                            {selectedExistingRepo && (
                              <p className="text-xs text-muted-foreground mt-2">
                                Files will be pushed to the <strong>main</strong> branch
                              </p>
                            )}
                          </div>
                        )}

                        <div className="flex items-start gap-3 p-4 bg-muted/30 rounded-lg">
                          <ExternalLink className="w-5 h-5 text-primary flex-shrink-0 mt-0.5" />
                          <div>
                            <p className="text-sm font-medium">Push to GitHub</p>
                            <p className="text-xs text-muted-foreground mt-1">
                              Your project will be generated and pushed directly to the selected GitHub repository.
                            </p>
                          </div>
                        </div>
                      </>
                    )}
                  </TabsContent>
                </Tabs>
              </div>
            </div>
          </WizardStep>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-6xl mx-auto">
          <ProgressBar
            currentStep={currentStep}
            totalSteps={steps.length}
            steps={steps}
            onStepClick={handleStepNavigation}
            completedSteps={completedSteps}
          />

          {/* Keyboard Shortcuts Helper */}
          <div className="mt-4 flex items-center justify-end gap-2">
            <div className="flex items-center gap-2 text-xs text-muted-foreground">
              <Keyboard className="w-3.5 h-3.5" />
              <span>Keyboard shortcuts available</span>
              <HelpTooltip 
                content={getHelpContent('keyboardShortcuts')} 
                side="left"
                iconSize="sm"
              />
            </div>
          </div>

          {/* Resume Dialog */}
          <ResumeDialog
            open={showResumeDialog}
            onResume={handleResume}
            onStartFresh={handleStartFresh}
            timestamp={savedTimestamp}
          />

          <div className="mt-8">
            {isGenerating ? (
              <Card className="w-full">
                <CardContent className="pt-8 pb-8 text-center">
                  <div className="space-y-6">
                    <div className="flex items-center justify-center">
                      <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
                    </div>
                    <div className="space-y-2">
                      <h3 className="text-xl font-semibold text-foreground">Generating Your Project</h3>
                      <p className="text-muted-foreground">
                        Creating project structure, dependencies, and sample code...
                      </p>
                    </div>
                    <div className="w-full bg-muted rounded-full h-2">
                      <div className="bg-primary h-2 rounded-full animate-pulse" style={{ width: '75%' }}></div>
                    </div>
                    <p className="text-sm text-muted-foreground">
                      This usually takes a few seconds. Your download will start automatically.
                    </p>
                  </div>
                </CardContent>
              </Card>
            ) : (
              renderStepContent()
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

