import { useState } from "react";
import Header from "@/components/Header";
import Footer from "@/components/Footer";
import LandingPage from "@/components/LandingPage";
import Wizard from "@/components/Wizard";
import { useToast } from "@/hooks/use-toast";

type AppState = 'landing' | 'wizard' | 'generating';

interface GitHubOptions {
  repoName: string;
  isNewRepo: boolean;
  isPrivate: boolean;
}

interface WizardConfig {
  testingType: string;
  framework: string;
  language: string;
  testingPattern: string;
  testRunner: string;
  buildTool: string;
  projectName: string;
  groupId?: string;
  artifactId?: string;
  cicdTool: string;
  reportingTool: string;
  utilities: {
    configReader: boolean;
    jsonReader: boolean;
    screenshotUtility: boolean;
    logger: boolean;
    dataProvider: boolean;
  };
  dependencies: string[];
  deliveryMethod?: 'download' | 'github';
  githubOptions?: GitHubOptions;
}

export default function Home() {
  const [appState, setAppState] = useState<AppState>('landing');
  const { toast } = useToast();

  const handleStartGeneration = () => {
    setAppState('wizard');
  };

  const handleBackToLanding = () => {
    setAppState('landing');
  };

  const handleDownload = async (config: WizardConfig) => {
    const response = await fetch('/api/generate-project', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(config),
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const contentType = response.headers.get('content-type');

    if (contentType?.includes('application/json')) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Project generation failed');
    }

    const blob = await response.blob();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${config.projectName}.zip`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    toast({
      title: "Project Generated Successfully!",
      description: `${config.projectName} has been downloaded to your device.`,
    });
  };

  const handleGitHubPush = async (config: WizardConfig) => {
    if (!config.githubOptions) {
      throw new Error('GitHub options not configured');
    }

    const response = await fetch('/api/github/push', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        config: {
          testingType: config.testingType,
          framework: config.framework,
          language: config.language,
          testingPattern: config.testingPattern,
          testRunner: config.testRunner,
          buildTool: config.buildTool,
          projectName: config.projectName,
          groupId: config.groupId,
          artifactId: config.artifactId,
          cicdTool: config.cicdTool,
          reportingTool: config.reportingTool,
          utilities: config.utilities,
          dependencies: config.dependencies,
        },
        repoName: config.githubOptions.repoName,
        isNewRepo: config.githubOptions.isNewRepo,
        isPrivate: config.githubOptions.isPrivate,
        branch: 'main',
      }),
    });

    const data = await response.json();

    if (!response.ok || !data.success) {
      throw new Error(data.message || 'Failed to push to GitHub');
    }

    toast({
      title: "Pushed to GitHub Successfully!",
      description: (
        <div className="space-y-1">
          <p>Your project has been pushed to GitHub.</p>
          <a 
            href={data.data.repoUrl} 
            target="_blank" 
            rel="noopener noreferrer"
            className="text-primary underline hover:no-underline"
          >
            View repository
          </a>
        </div>
      ),
    });
  };

  const handleWizardComplete = async (config: WizardConfig) => {
    setAppState('generating');

    try {
      console.log('Generating project with config:', config);

      if (config.deliveryMethod === 'github') {
        await handleGitHubPush(config);
      } else {
        await handleDownload(config);
      }

      setAppState('landing');
    } catch (error) {
      console.error('Error generating project:', error);
      toast({
        title: "Generation Failed",
        description: error instanceof Error ? error.message : "There was an error generating your project. Please try again.",
        variant: "destructive",
      });
      setAppState('wizard');
    }
  };

  return (
    <div className="min-h-screen bg-background flex flex-col">
      <Header onLogoClick={handleBackToLanding} />

      <main className="flex-1">
        {appState === 'landing' && (
          <LandingPage onStartGeneration={handleStartGeneration} />
        )}

        {appState === 'wizard' && (
          <Wizard 
            onComplete={handleWizardComplete}
            onBack={handleBackToLanding}
          />
        )}

        {appState === 'generating' && (
          <div className="flex items-center justify-center min-h-[60vh]">
            <div className="text-center space-y-4">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
              <h2 className="text-xl font-semibold">Generating Your Project...</h2>
              <p className="text-muted-foreground">This may take a few seconds</p>
            </div>
          </div>
        )}
      </main>

      <Footer />
    </div>
  );
}

