import { useState, useEffect } from 'react';

const STORAGE_KEY = 'qastarter-wizard-config';
const STORAGE_VERSION = '1.0';

interface SavedConfig {
  version: string;
  timestamp: number;
  config: Record<string, any>;
  currentStep: number;
}

export function useConfigPersistence() {
  const [hasSavedConfig, setHasSavedConfig] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    setHasSavedConfig(!!saved);
  }, []);

  const saveConfig = (config: Record<string, any>, currentStep: number) => {
    try {
      const data: SavedConfig = {
        version: STORAGE_VERSION,
        timestamp: Date.now(),
        config,
        currentStep,
      };
      localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
    } catch (error) {
      console.error('Failed to save configuration:', error);
    }
  };

  const loadConfig = (): SavedConfig | null => {
    try {
      const saved = localStorage.getItem(STORAGE_KEY);
      if (!saved) return null;

      const data: SavedConfig = JSON.parse(saved);

      // Validate version compatibility
      if (data.version !== STORAGE_VERSION) {
        console.warn('Saved configuration version mismatch, clearing...');
        clearConfig();
        return null;
      }

      return data;
    } catch (error) {
      console.error('Failed to load configuration:', error);
      return null;
    }
  };

  const clearConfig = () => {
    try {
      localStorage.removeItem(STORAGE_KEY);
      setHasSavedConfig(false);
    } catch (error) {
      console.error('Failed to clear configuration:', error);
    }
  };

  return {
    hasSavedConfig,
    saveConfig,
    loadConfig,
    clearConfig,
  };
}

