/**
 * GitHub Integration Service
 * Uses Replit's GitHub connector for authentication and Octokit for API calls
 */

import { Octokit } from '@octokit/rest';

let connectionSettings: any;

async function getAccessToken(): Promise<string> {
  if (connectionSettings && connectionSettings.settings.expires_at && new Date(connectionSettings.settings.expires_at).getTime() > Date.now()) {
    return connectionSettings.settings.access_token;
  }

  const hostname = process.env.REPLIT_CONNECTORS_HOSTNAME;
  const xReplitToken = process.env.REPL_IDENTITY 
    ? 'repl ' + process.env.REPL_IDENTITY 
    : process.env.WEB_REPL_RENEWAL 
    ? 'depl ' + process.env.WEB_REPL_RENEWAL 
    : null;

  if (!xReplitToken) {
    throw new Error('GitHub integration not available in this environment');
  }

  connectionSettings = await fetch(
    'https://' + hostname + '/api/v2/connection?include_secrets=true&connector_names=github',
    {
      headers: {
        'Accept': 'application/json',
        'X_REPLIT_TOKEN': xReplitToken
      }
    }
  ).then(res => res.json()).then(data => data.items?.[0]);

  const accessToken = connectionSettings?.settings?.access_token || connectionSettings?.settings?.oauth?.credentials?.access_token;

  if (!connectionSettings || !accessToken) {
    throw new Error('GitHub not connected. Please connect your GitHub account first.');
  }
  return accessToken;
}

// WARNING: Never cache this client. Access tokens expire.
async function getGitHubClient(): Promise<Octokit> {
  const accessToken = await getAccessToken();
  return new Octokit({ auth: accessToken });
}

export interface GitHubUser {
  login: string;
  name: string | null;
  avatar_url: string;
}

export interface GitHubRepo {
  id: number;
  name: string;
  full_name: string;
  private: boolean;
  html_url: string;
  description: string | null;
  default_branch: string;
}

export interface CreateRepoOptions {
  name: string;
  description?: string;
  isPrivate?: boolean;
  autoInit?: boolean;
}

export interface PushToRepoOptions {
  owner: string;
  repo: string;
  branch?: string;
  files: Array<{ path: string; content: string }>;
  commitMessage?: string;
}

/**
 * Check if GitHub is connected
 */
export async function isGitHubConnected(): Promise<boolean> {
  try {
    await getAccessToken();
    return true;
  } catch {
    return false;
  }
}

/**
 * Get authenticated user info
 */
export async function getAuthenticatedUser(): Promise<GitHubUser> {
  const octokit = await getGitHubClient();
  const { data } = await octokit.users.getAuthenticated();
  return {
    login: data.login,
    name: data.name,
    avatar_url: data.avatar_url,
  };
}

/**
 * List user's repositories
 */
export async function listUserRepos(page = 1, perPage = 30): Promise<GitHubRepo[]> {
  const octokit = await getGitHubClient();
  const { data } = await octokit.repos.listForAuthenticatedUser({
    sort: 'updated',
    direction: 'desc',
    per_page: perPage,
    page,
    affiliation: 'owner',
  });

  return data.map(repo => ({
    id: repo.id,
    name: repo.name,
    full_name: repo.full_name,
    private: repo.private,
    html_url: repo.html_url,
    description: repo.description,
    default_branch: repo.default_branch || 'main',
  }));
}

/**
 * Create a new repository
 */
export async function createRepository(options: CreateRepoOptions): Promise<GitHubRepo> {
  const octokit = await getGitHubClient();

  const { data } = await octokit.repos.createForAuthenticatedUser({
    name: options.name,
    description: options.description || `QA Automation project generated by QAStarter`,
    private: options.isPrivate ?? false,
    auto_init: false, // We'll push our own files
  });

  return {
    id: data.id,
    name: data.name,
    full_name: data.full_name,
    private: data.private,
    html_url: data.html_url,
    description: data.description,
    default_branch: data.default_branch || 'main',
  };
}

/**
 * Push files to a repository
 */
export async function pushToRepository(options: PushToRepoOptions): Promise<{ commitSha: string; htmlUrl: string }> {
  const octokit = await getGitHubClient();
  const { owner, repo, files, commitMessage = 'Initial commit from QAStarter' } = options;
  const branch = options.branch || 'main';

  // Create blobs for each file
  const blobs = await Promise.all(
    files.map(async (file) => {
      const { data } = await octokit.git.createBlob({
        owner,
        repo,
        content: Buffer.from(file.content).toString('base64'),
        encoding: 'base64',
      });
      return { path: file.path, sha: data.sha, mode: '100644' as const, type: 'blob' as const };
    })
  );

  // Try to get the latest commit SHA, or create initial commit for empty repo
  let baseSha: string | null = null;
  let baseTreeSha: string | null = null;

  try {
    const { data: ref } = await octokit.git.getRef({
      owner,
      repo,
      ref: `heads/${branch}`,
    });
    baseSha = ref.object.sha;

    const { data: commit } = await octokit.git.getCommit({
      owner,
      repo,
      commit_sha: baseSha,
    });
    baseTreeSha = commit.tree.sha;
  } catch (error: any) {
    // Branch doesn't exist yet, this is a new repo
    if (error.status !== 404) {
      throw error;
    }
  }

  // Create tree with all files
  const treeParams: any = {
    owner,
    repo,
    tree: blobs,
  };

  if (baseTreeSha) {
    treeParams.base_tree = baseTreeSha;
  }

  const { data: tree } = await octokit.git.createTree(treeParams);

  // Create commit
  const commitParams: any = {
    owner,
    repo,
    message: commitMessage,
    tree: tree.sha,
  };

  if (baseSha) {
    commitParams.parents = [baseSha];
  } else {
    commitParams.parents = [];
  }

  const { data: commit } = await octokit.git.createCommit(commitParams);

  // Create or update the reference
  try {
    if (baseSha) {
      await octokit.git.updateRef({
        owner,
        repo,
        ref: `heads/${branch}`,
        sha: commit.sha,
      });
    } else {
      await octokit.git.createRef({
        owner,
        repo,
        ref: `refs/heads/${branch}`,
        sha: commit.sha,
      });
    }
  } catch (error: any) {
    // If ref doesn't exist, create it
    if (error.status === 422) {
      await octokit.git.createRef({
        owner,
        repo,
        ref: `refs/heads/${branch}`,
        sha: commit.sha,
      });
    } else {
      throw error;
    }
  }

  return {
    commitSha: commit.sha,
    htmlUrl: `https://github.com/${owner}/${repo}`,
  };
}

/**
 * Check if a repository exists
 */
export async function repoExists(owner: string, repo: string): Promise<boolean> {
  try {
    const octokit = await getGitHubClient();
    await octokit.repos.get({ owner, repo });
    return true;
  } catch (error: any) {
    if (error.status === 404) {
      return false;
    }
    throw error;
  }
}

